import React,{Component, useState, useEffect} from 'react';
import {Card, Container} from 'react-bootstrap';
import styles from './Tasks.module.css';
import results from '../results'
import {useAuth} from '../../context/AuthContext'
 function Tasks() {
     const [items, setItem] = useState({});
     const {currentUser} = useAuth();
    // state={
    //     results: [],
    // }

    // const useEffect =()=>{
    //     results.get('/task.json').then(response=>{
    //         console.log(response.data)
    //         const fetchedResults = [];
    //             for(let key in response.data){
    //                 fetchedResults.unshift({
    //                     ...response.data[key],
    //                     id: key,
    //                 }
    //                 )
    //             }
    //             setResult({results: fetchedResults})
    //     })
    // }
    useEffect(()=>{
        results.get('/task.json').then(response=>{
            console.log(response.data)
            const fetchedResults = [];
                for(let key in response.data){
                    fetchedResults.unshift({
                        ...response.data[key],
                        id: key,
                    }
                    )
                }
                setItem({items: fetchedResults})
        })
        console.log(items)
        console.log(currentUser)
     },[])

     const itemsList  =   items.map(result=>{

         return 
         <div className="col-12 col-md-4" key={result.id}>
                <Card className={styles.Card} >
                    <Card.Body>
                        <div>
                            Deadline: {result.date}
                        </div>
                        <h2 className={styles.TaskTitle}>{result.title}</h2>
                        <div>
                            <p>{result.content}</p>
                        </div>

                        <div className="d-flex">
                            <button className={styles.DoneBtn}>Done</button>
                            <button className={styles.DeleteBtn}>Delete</button>
                            <button className={styles.EditBtn}>Edit</button>
                        </div>
                    </Card.Body>
                </Card>
            </div>
     }

    )

        return (
            <Container>
                {itemsList}
            </Container>
        )

    }
    export default Tasks;
