import React,{Component} from 'react';
import {Form, Button, Container} from 'react-bootstrap';
import results from '../results';
import {Link} from 'react-router-dom';
import Dashboard from '../Dashboard';

class TaskForm extends Component{
    constructor(props) {
        super(props);
        let date = new Date();

        let formatedDate = `${date.getMonth()+1}-${date.getDate()}-${date.getFullYear()}`
        this.state = {
          // Use object destructuring to create a new object with the default value
                title: '',
                content: '',
                date: formatedDate

        };
      }

  postHandler = (e) =>{
    e.preventDefault();
    const data = {
        title: this.state.title,
        content: this.state.content,
        date: this.state.date
    }
    results.post('/task.json', data).then(response=>{
        console.log(response);
    }).catch(error=>{
        console.log(error);
    })
  }
  taskHandler = ()=>{
    results.get( '/task.json' )
    .then( res => {
        const fetchedOrders = [];
        for ( let key in res.data ) {
            fetchedOrders.push( {
                ...res.data[key],
                id: key
            } );
        }
      console.log(fetchedOrders)
    } )
    .catch( err => {
        console.log(err)
    } );
  }
    render() {
       return(
            <>
            <Dashboard/>
            <Container>
                <div className="row py-4 justify-content-center">
                    <h2>Add a new task here</h2>
                </div>
                <div className="row">
                    <Form onSubmit={this.postHandler} className="w-100">
                        <Form.Group id="title">
                            <Form.Label>Task Title</Form.Label>
                            <Form.Control type="text"  required placeholder="Title" value={this.state.title} onChange={(e)=> this.setState({title: e.target.value})}/>
                        </Form.Group>
                        <Form.Group id="content">
                            <Form.Label>Task Content</Form.Label>
                            <Form.Control  as="textarea" rows={3} required placeholder="Task" value={this.state.content} onChange={(e)=> this.setState({content: e.target.value})}/>
                        </Form.Group>
                        <Form.Group id="date">
                            <Form.Label>Pick a deadline</Form.Label>
                            <Form.Control type="datetime-local" value={this.state.date} onChange={(e)=> this.setState({date: e.target.value})}></Form.Control>
                        </Form.Group>
                        <div>
                            <Button type="submit">Submit</Button>
                            <Link to="/">Cancel</Link>
                        </div>
                    </Form>
                </div>
            </Container>
            </>
       )
    }
}
export default TaskForm;
