import React,{useState, useEffect} from 'react';
import {Card, Container} from 'react-bootstrap';
import styles from './Tasks.module.css';
import {useAuth} from '../../context/AuthContext'
import results from '../results';
import Buttons from '../Buttons/Buttons';
import firebase from '../../firebase'
 function Tasks(){
     const [items, setItems] = useState([]);
     const {currentUser} = useAuth();
    const db = firebase.database().ref('task/' + currentUser)
    console.log(db)
    useEffect(()=>{
        results.get('/task.json').then(response=>{
            console.log(response.data)
            const fetchedResults = [];
                for(let key in response.data){
                    fetchedResults.unshift({
                        ...response.data[key],
                        id: key,
                    }
                    )
                }
            setItems(fetchedResults)
        })
     },[])
    const handleDone = () =>{
        db.child('task').update({status : 'done'})
    }

        return (
            <Container>
                <div className="row d-flex justify-content-center text-center pt-4">
                    {items.map(result=>(
                        result.user === currentUser.uid &&
                        <div className="col-12 col-md-4" key={result.id}>
                        <Card className={styles.Card} >
                        <Card.Body>
                            <div>
                                {result.date}
                            </div>
                            <h2 className={styles.TaskTitle}>{result.title}</h2>
                            <div>
                                <p>{result.content}</p>
                            </div>

                            <div className="d-flex">
                                <Buttons click={handleDone} class={styles.DoneBtn}>Done</Buttons>
                                <Buttons  class={styles.DeleteBtn}>Delete</Buttons>
                                <Buttons  class={styles.EditBtn}>Edit</Buttons>
                            </div>
                        </Card.Body>
                    </Card>
                    </div>
                    )) }
                </div>
            </Container>
        )
 }
    export default Tasks;
