import React,{Component} from 'react';
import {Form, Button} from 'react-bootstrap';
import results from '../results';
import {Link} from 'react-router-dom';

class TaskForm extends Component{

    state = {
        title: '',
        content: ''
    }
  postHandler = (e) =>{
    e.preventDefault();
    const data = {
        title: this.state.title,
        content: this.state.content,
    }
    results.post('/task.json', data).then(response=>{
        console.log(response);
    }).catch(error=>{
        console.log(error);
    })

  }
  taskHandler = ()=>{
    results.get( '/task.json' )
    .then( res => {
        const fetchedOrders = [];
        for ( let key in res.data ) {
            fetchedOrders.push( {
                ...res.data[key],
                id: key
            } );
        }
      console.log(fetchedOrders)
    } )
    .catch( err => {
        console.log(err)
    } );
  }
    render() {
       return(
           <>
        <Form onSubmit={this.postHandler}>
            <Form.Group id="title">
                <Form.Label>Task Title</Form.Label>
                <Form.Control type="text"  required placeholder="Title" value={this.state.title} onChange={(e)=> this.setState({title: e.target.value})}/>
            </Form.Group>
            <Form.Group id="content">
                <Form.Label>Task Content</Form.Label>
                <Form.Control  type="textarea" required placeholder="Task" value={this.state.content} onChange={(e)=> this.setState({content: e.target.value})}/>
            </Form.Group>
            <div>
                <Button type="submit">Submit</Button>
                <Link to="/">Cancel</Link>
            </div>
        </Form>
        </>
       )
    }
}
export default TaskForm;
